USE nyc_taxi_discovery;

SELECT * FROM OPENROWSET(
    BULK 'abfss://nyc-taxi-data@synapsecoursedlshreyas.dfs.core.windows.net/raw/vendor_unquoted.csv',
    FORMAT = 'CSV',
    PARSER_VERSION = '2.0',
    HEADER_ROW = TRUE
) AS vendor;

SELECT * FROM OPENROWSET(
    BULK 'abfss://nyc-taxi-data@synapsecoursedlshreyas.dfs.core.windows.net/raw/vendor_escaped.csv',
    FORMAT = 'CSV',
    PARSER_VERSION = '2.0',
    HEADER_ROW = TRUE, 
    ESCAPECHAR = '\\' --to escape comma after Technologies in row 1
) AS vendor;
