USE nyc_taxi_discovery;

SELECT * FROM OPENROWSET(
    BULK 'abfss://nyc-taxi-data@synapsecoursedlshreyas.dfs.core.windows.net/raw/vendor_unquoted.csv',
    FORMAT = 'CSV',
    PARSER_VERSION = '2.0',
    HEADER_ROW = TRUE
) AS vendor;

SELECT * FROM OPENROWSET(
    BULK 'abfss://nyc-taxi-data@synapsecoursedlshreyas.dfs.core.windows.net/raw/vendor_escaped.csv',
    FORMAT = 'CSV',
    PARSER_VERSION = '2.0',
    HEADER_ROW = TRUE, 
    ESCAPECHAR = '\\' --to escape comma after Technologies in row 1
) AS vendor;

USE nyc_taxi_discovery;

SELECT * FROM OPENROWSET(
        BULK 'abfss://nyc-taxi-data@synapsecoursedlshreyas.dfs.core.windows.net/raw/trip_type.tsv',
        FORMAT = 'CSV', PARSER_VERSION = '2.0', HEADER_ROW = TRUE,
         HEADER_ROW = TRUE, FIELDTERMINATOR = '\t'
    ) AS trip_type;

USE nyc_taxi_discovery;

SELECT CAST(JSON_VALUE(jsonDoc,'$.payment_type') AS SMALLINT) payment_type,
CAST(JSON_VALUE(jsonDoc,'$.payment_type_desc') AS VARCHAR(15)) payment_type_desc
 FROM OPENROWSET(
        BULK 'abfss://nyc-taxi-data@synapsecoursedlshreyas.dfs.core.windows.net/raw/payment_type.json',
        FORMAT = 'CSV', PARSER_VERSION = '1.0',  FIELDTERMINATOR = '0x0b', FIELDQUOTE = '0x0b',
        ROWTERMINATOR = '0x0a'
    ) WITH(
        jsonDoc NVARCHAR(MAX)
    ) AS payment_type;

